mapper.select:
select
  article_id, article_name, user_id, content, view_count, like, state, create_time, update_time
from blog.article


        and article_id = #{articleId}


        and article_name = #{articleName}


        and user_id = #{userId}


        and content = #{content}


        and view_count = #{viewCount}


        and like = #{like}


        and state = #{state}


        and create_time = #{createTime}


        and update_time = #{updateTime}
